import Draggable from 'react-draggable';
import ControlBar from './ControlBar.js';

function OpeningFile() {
    const offSetX = 150;
    const offSetY = 20;
    const width = window.innerWidth - (offSetX * 2);

    return (
        <div style={`width:${width}px;`}>
            <Draggable
                handle=".handle"
                className="absolute"
                defaultPosition={{x: offSetX, y: offSetY}}
            >
                <div>
                    <ControlBar/>
                    <div className="flex flex-col p-10 bg-white border-x-2 border-b-2 border-slate-200 text-black text-center">
                        <h1 className="text-8xl">
                            Nicholas Angelici
                        </h1>
                        <p className="text-black text-lg">Hello! I'm Nicholas Angelici, a student at UC Berkeley double majoring in Computer Science and Data Science</p>
                    </div>
                </div>
            </Draggable>
        </div>
    );
}

export default OpeningFile;
