import Draggable from 'react-draggable';
import { useState } from "react";

function FileIcon({highlight, setHighlight, Icon, Component, name, color, onLoad}) {
    const [display, setDisplay] = useState(onLoad);
    color = !color ? "white" : color;
    function updateHighlight () {
        setHighlight(name);
        console.log(highlight);
    }

    return (
        <div onDoubleClick={() => !display ? setDisplay(!display) : null} onClick={updateHighlight}>
            <Draggable>
                <div className={`flex flex-col items-center`}>
                    <Icon color={color} size={64} />
                    <p className={`${color == "white" ? "text-white" : `text-${color}-500`} ${highlight == name ? 'bg-slate-50' : 'bg-black'}`}>
                        {name}
                    </p>
                </div>
            </Draggable>
            {display ? <Component setDisplay={setDisplay}/> : null}
        </div>
    )
}

export default FileIcon;
