import './2048/2048.css';
import './2048/2048.js';
import { useEffect } from 'react';
import ControlBar from '../ControlBar.jsx';
import Draggable from 'react-draggable';

function The2048() {
    /*useEffect(() => {
        // Dynamically load your game script
        const script = document.createElement('script');
        script.src = '/2048/2048.js';
        script.async = true;
        script.onload = () => {
          // Call the function that initializes your game
          if (window.start) {
            window.start();
          }
        };
        document.body.appendChild(script);

        // Cleanup script on component unmount
        return () => {
          document.body.removeChild(script);
        };
    }, []);*/


    return (
      <Draggable
        handle=".handle"
        bounds={{top:0, bottom:null, left:null, right:null}}
      >
        <ControlBar />
        <div className="">
            <div class = "overflow-container">
            <div class = "dashboard">
                <div class = "score">Score: 0</div>
                <div class = "moves-made">Moves Made: 0</div>
                <button class = "reset">Restart</button>
            </div>
            <div class = "game-container">
                <div class = "grid">
                </div>
                <div class = "tile-container">
                </div>
                <div class = "win-message">Congratulations!<br />Click to continue</div>
                <div class = "lose-message">Game Over!<br />Click "Restart" to play again</div>
            </div>
            </div>
        </div>
      </Draggable>
    )
}

export default The2048;
