import Draggable from 'react-draggable';
import { useState } from "react";

function FileIcon({highlight, setHighlight, Icon, name, color, display, setDisplay}) {
    color = !color ? "black" : color;
    function updateHighlight () {
        setHighlight(name);
        console.log(highlight);
    }

    return (
        <div>
            <Draggable>
                <div className={`flex flex-col items-center`} onDoubleClick={() => !display ? setDisplay(!display) : null} onClick={updateHighlight}>
                    <Icon color={"black"} fill={"white"} size={64} />
                    <p className={`px-1 rounded-sm ${color == "black" ? "text-black" : `text-${color}-500`} ${highlight == name ? 'bg-neutral' : 'bg-white'}`}>
                        {name}
                    </p>
                </div>
            </Draggable>
        </div>
    )
}

export default FileIcon;
