import Draggable from 'react-draggable';
import ControlBar from '../ControlBar.jsx';

function OpeningFile(props) {
    const offSetX = 150;
    const offSetY = -60;
    const width = window.innerWidth - (offSetX * 2);

    return (
        <div style={{width:`${width}` + 'px'}} className="absolute">
            <Draggable
                handle=".handle"
                defaultPosition={{x: offSetX, y: offSetY}}
            >
                <div className="rounded-sm">
                    <ControlBar setDisplay={props.setDisplay}/>
                    <div className="flex flex-col rounded-bl-md rounded-br-md overflow-auto p-10 bg-white border-x-4 border-b-4 border-slate-200 text-black text-center">
                        <h1 className="text-8xl">
                            Nicholas Angelici
                        </h1>
                        <p className="text-black text-lg">Hello! I'm Nicholas Angelici, a student at UC Berkeley double majoring in Computer Science and Data Science</p>
                    </div>
                </div>
            </Draggable>
        </div>
    );
}

export default OpeningFile;
