function ControlBar({setDisplay, name}) {
    return (
        <div className="handle">
            <div className="border-4 border-black rounded-tl-md rounded-tr-md w-full bg-white flex flex-row p-2">
                <div className="flex flex-row gap-2">
                    <div className="w-4 h-4 rounded-full bg-red-600 hover:bg-red-800" onClick={() => setDisplay(false)}/>
                    <div className="w-4 h-4 rounded-full bg-green-500"/>
                </div>
                <div className="absolute flex flex-row w-full justify-center pr-8">
                    <h4 className="italic text-sm font-bold text-neutral-500">{name}</h4>
                </div>
            </div>
        </div>
    )
}

export default ControlBar;
