import React, { useState, useEffect } from 'react';
import './App.css';

const colors = {
  "4": "#3385FF",
  "8": "#3365FF",
  "16": "#3345FF",
  "32": "#3325FF",
  "64": "#3305FF",
  "128": "#7a05FF",
  "256": "#e00000",
  "512": "#a00000",
  "1024": "#4c03f4",
  "2048": "#b433ff",
  "4096": "#000000",
};

const the2048 = () => {
  const [grid, setGrid] = useState([]);
  const [score, setScore] = useState(0);
  const [moves, setMoves] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [win, setWin] = useState(false);

  useEffect(() => {
    buildGrid();
    placeFirstTiles();
    const handleKeyDown = (e) => {
      if (!gameOver) {
        clearMerged();
        if (!checkGameOver()) {
          moveBlocks(e.code);
          if (moved) {
            setMoves(moves + 1);
            placeNewTile();
          }
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keydown', preventDefaultForKeys, false);
    window.addEventListener('click', () => setWin(false));
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keydown', preventDefaultForKeys, false);
      window.removeEventListener('click', () => setWin(false));
    };
  }, [gameOver, moves]);

  const preventDefaultForKeys = (e) => {
    if (["Space", "ArrowUp", "ArrowDown", "ArrowLeft", "ArrowRight"].indexOf(e.code) > -1) {
      e.preventDefault();
    }
  };

  const buildGrid = () => {
    let initialGrid = [];
    for (let i = 0; i < 4; i++) {
      initialGrid.push(new Array(4).fill(null));
    }
    setGrid(initialGrid);
  };

  const placeNewTile = () => {
    let newRow, newColumn;
    do {
      newRow = Math.trunc(Math.random() * 4);
      newColumn = Math.trunc(Math.random() * 4);
    } while (grid[newRow][newColumn] !== null);

    let newTileValue = Math.random() < 0.9 ? 2 : 4;
    let newGrid = [...grid];
    newGrid[newRow][newColumn] = newTileValue;
    setGrid(newGrid);
  };

  const placeMergedTile = (row, column, num) => {
    let newGrid = [...grid];
    newGrid[row][column] = num;
    setGrid(newGrid);
    setScore(score + num);
    if (num === 2048) {
      setWin(true);
    }
  };

  const placeFirstTiles = () => {
    placeNewTile();
    placeNewTile();
  };

  const moveBlock = (block, path, dir) => {
    let [blockRow, blockColumn] = block;
    let newGrid = [...grid];
    if (path === "horizontal") {
      while (blockColumn >= 0 && blockColumn < 4) {
        const newSpot = newGrid[blockRow][blockColumn + dir];
        if (newSpot !== null) {
          if (newSpot === newGrid[blockRow][blockColumn] && !newSpot.merged) {
            placeMergedTile(blockRow, blockColumn + dir, newGrid[blockRow][blockColumn] * 2);
          }
          break;
        }
        if (blockColumn + dir < 0 || blockColumn + dir >= 4) break;
        blockColumn += dir;
      }
    } else if (path === "vertical") {
      while (blockRow >= 0 && blockRow < 4) {
        const newSpot = newGrid[blockRow + dir][blockColumn];
        if (newSpot !== null) {
          if (newSpot === newGrid[blockRow][blockColumn] && !newSpot.merged) {
            placeMergedTile(blockRow + dir, blockColumn, newGrid[blockRow][blockColumn] * 2);
          }
          break;
        }
        if (blockRow + dir < 0 || blockRow + dir >= 4) break;
        blockRow += dir;
      }
    }
    if (grid[blockRow][blockColumn] !== newGrid[blockRow][blockColumn]) {
      moved = true;
    }
    newGrid[blockRow][blockColumn] = null;
    newGrid[blockRow][blockColumn] = newGrid[blockRow][blockColumn];
    setGrid(newGrid);
  };

  const moveBlocks = (code) => {
    moved = false;
    let tiles = [];
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        if (grid[i][j] !== null) {
          tiles.push([i, j]);
        }
      }
    }
    switch (code) {
      case "ArrowRight":
        tiles.reverse().forEach((tile) => moveBlock(tile, "horizontal", 1));
        break;
      case "ArrowLeft":
        tiles.forEach((tile) => moveBlock(tile, "horizontal", -1));
        break;
      case "ArrowUp":
        tiles.forEach((tile) => moveBlock(tile, "vertical", -1));
        break;
      case "ArrowDown":
        tiles.reverse().forEach((tile) => moveBlock(tile, "vertical", 1));
        break;
      default:
    }
  };

  const clearMerged = () => {
    let newGrid = grid.map(row => row.map(tile => tile ? { ...tile, merged: false } : null));
    setGrid(newGrid);
  };

  const checkGameOver = () => {
    let tiles = [];
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        if (grid[i][j] !== null) {
          tiles.push([i, j, grid[i][j]]);
        }
      }
    }
    if (tiles.length === 16) {
      for (let i = 0; i < 16; i++) {
        if (i % 4 !== 3 && tiles[i][2] === tiles[i + 1][2]) {
          setGameOver(false);
          return false;
        }
        if (i < 12 && tiles[i][2] === tiles[i + 4][2]) {
          setGameOver(false);
          return false;
        }
      }
      setGameOver(true);
      return true;
    }
    return false;
  };

  const reset = () => {
    setGrid([]);
    setScore(0);
    setMoves(0);
    setGameOver(false);
    setWin(false);
    buildGrid();
    placeFirstTiles();
  };

  return (
    <div className="overflow-container">
      <Dashboard score={score} moves={moves} onReset={reset} />
      <GameContainer grid={grid} />
      {win && <WinMessage />}
      {gameOver && <LoseMessage />}
    </div>
  );
};

const Dashboard = ({ score, moves, onReset }) => (
  <div className="dashboard">
    <div className="score">Score: {score}</div>
    <div className="moves-made">Moves Made: {moves}</div>
    <button className="reset" onClick={onReset}>Restart</button>
  </div>
);

const GameContainer = ({ grid }) => (
  <div className="game-container">
    <div className="grid">
      {grid.map((row, rowIndex) =>
        row.map((cell, colIndex) => (
          <div key={`${rowIndex}-${colIndex}`} className={`grid-block ${cell !== null ? `pos-${rowIndex + 1}-${colIndex + 1}` : ''}`}>
            {cell !== null && <div className="tile" style={{ backgroundColor: colors[cell] }}>{cell}</div>}
          </div>
        ))
      )}
    </div>
  </div>
);

const WinMessage = () => (
  <div className="win-message">
    Congratulations!<br />Click to continue
  </div>
);

const LoseMessage = () => (
  <div className="lose-message">
    Game Over!<br />Click "Restart" to play again
  </div>
);

export default the2048;
