import Draggable from 'react-draggable';
import { useState } from "react";

function FileIcon({highlight, setHighlight, Icon, name, color, display, setDisplay}) {
    color = !color ? "black" : color;
    function updateHighlight () {
        setHighlight(name);
        //console.log(highlight);
    }

    function updateDisplay () {
        if (!display) {
            setDisplay(!display);
        } 
    }

    return (
        <div>
            <Draggable
                bounds={{top:-20, bottom:null, left:null, right:null}}
            >
                <div className={`flex flex-col items-center absolute`} onDoubleClick={updateDisplay} onMouseDownCapture={updateHighlight}>
                    <Icon color={`${highlight == name ? 'white' : 'black'}`} fill={`${highlight == name ? 'black' : 'white'}`} size={64} />
                    <p className={`px-1 rounded-sm ${color == "black" ? "text-black" : `text-${color}-500`} ${highlight == name ? 'bg-black text-white' : 'bg-white'}`}>
                        {name}
                    </p>
                </div>
            </Draggable>
        </div>
    )
}

export default FileIcon;
