import Start2048 from './2048/2048';
import { useEffect, useRef } from 'react';
import ControlBar from '../ControlBar.jsx';
import Draggable from 'react-draggable';

function The2048() {
  const gameContainerRef = useRef(null);

  useEffect(() => {
    // Function to initialize game logic
    const startGame = () => {
      if (gameContainerRef.current) {
        // Call initializeGame with the game container reference
        initializeGame(gameContainerRef.current);
      }
    };

    // Call startGame when component mounts
    startGame();

    // Cleanup: Remove event listeners and clean up resources as needed
    return () => {
      // Example: Remove event listener if necessary (not always required)
      if (gameContainerRef.current) {
        gameContainerRef.current.removeEventListener('keydown', handleKeyDown);
      }
    };
  }, []);

  return (
    <Draggable
      handle=".handle"
      bounds={{top:0, bottom:null, left:null, right:null}}
    >
      <div>
      <ControlBar />
        <div id="game-container" ref={gameContainerRef}>
            <div class = "overflow-container">
            <div class = "dashboard">
                <div class = "score">Score: 0</div>
                <div class = "moves-made">Moves Made: 0</div>
                <button class = "reset">Restart</button>
            </div>
            <div class = "game-container">
                <div class = "grid">
                </div>
                <div class = "tile-container">
                </div>
                <div class = "win-message">Congratulations!<br />Click to continue</div>
                <div class = "lose-message">Game Over!<br />Click "Restart" to play again</div>
            </div>
            </div>
        </div>
      </div>
    </Draggable>
  )
}

export default The2048;
