import Draggable from 'react-draggable';
import ControlBar from '../ControlBar.jsx';

function OpeningFile({display, setDisplay}) {
    const offSetX = 40;
    const offSetY = 20;
    // const width = window.innerWidth - (offSetX * 2);

    return (
        display ?
        <div>
            <Draggable
                handle=".handle"
                defaultPosition={{x: offSetX, y: offSetY}}
            >
                <div className="absolute rounded-sm max-h-fit w-6/12">
                    <ControlBar setDisplay={setDisplay}/>
                    <div className="flex flex-col rounded-bl-md rounded-br-md overflow-auto p-10 bg-white border-x-4 border-b-4 border-black text-black text-left">
                        <h1 className="text-8xl">
                            Nicholas Angelici
                        </h1>
                        <br />
                        <p className="text-black text-lg">
                            Hello! I'm Nicholas Angelici, a student at UC Berkeley double majoring 
                            in Computer Science and Data Science. 
                            When I'm not working on projects, 
                            I like to spend my time reading, playing golf,
                            going to the gym, and improving my rankings in Nintendo Switch Sports. 
                        </p>
                        <br />
                        <p className="text-black text-lg">
                            This is an interactive website inspiried by early mac desktops. So, click around!
                            Check out the files, and games that are offered.
                        </p>
                    </div>
                </div>
            </Draggable>
        </div>
        : null
    );
}

export default OpeningFile;
